import React from 'react'
import { Link } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';
import inputField from '../../../conFigForm/inputField';
import selectField from '../../../conFigForm/selectField';
function addPost() {

    const check = yup.object({
        title: yup.string().required('Not null'),
        link: yup.string().url('It is not the link').required('Not null'),
    });
    return (
        <div className='container-sm'>
            <div className='container-sm'>

                <div className='row justify-content-center '>
                    <div className='col-12 col-sm-8 col-lg-6 d-flex justify-content-between align-items-center p-0'>
                        <p style={{ color: '#4a50c0', fontWeight: '500' }} className='mt-4 text-center fs-4'>Add new tool</p>
                        <Link to='/post' className='btn btn-primary'>Back</Link>
                    </div>
                </div>
                <div className='row justify-content-center '>
                    <Formik
                        initialValues={{
                            title: '',
                            content: '',
                            link: '',
                            category: '',
                            image: ''
                        }}
                        validationSchema = {check}
                        validate = {value=>{
                            const error = {};
                            if(value.image === ''){
                                error.image = 'Not null'
                            }
                            else{
                                if(!value.image.toLowerCase().endsWith('.png' && '.jpg' && '.jpeg' && '.gif')){
                                    error.image = 'khong phai image'
                                }
                            }
                            return {
                                ...error
                            }
                        }}
                        onSubmit={(values, { setSubmitting }) => {
                            setTimeout(() => {
                                alert(JSON.stringify(values, null, 2));
                                setSubmitting(false);
                            }, 400);
                        }}
                    >
                        {({ isSubmitting }) => (
                            <Form className='col-12 col-sm-8 col-lg-6 border border-primary p-4 mt-3 rounded-2 shadow-sm'>
                                <div className="mb-3">
                                    <Field name='title' type='text' placeholder='enter title' label='Title' component={inputField} />
                                    <ErrorMessage className="form-text" style={{color: 'red'}} name='title' component='small'/>
                                </div>
                                <div className=" row row-cols-1 row-cols-sm-2">
                                    <div className='col'>
                                        <div className="mb-3">
                                            <Field name='link' type='url' placeholder='enter link' label='Link' component={inputField} />
                                            <ErrorMessage className="form-text" style={{color: 'red'}} name='link' component='small'/>
                                        </div>
                                    </div>
                                    <div className='col'>
                                        <div className="mb-3">
                                            <Field name='category' label='Category' component={selectField} />
                                        </div>
                                    </div>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="exampleInputPassword1" className="form-label">Content</label>
                                    <textarea type="password" className="form-control" id="exampleInputPassword1"></textarea>
                                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                                </div>
                                <div className="mb-3">
                                    <Field name='image' type='file' label='Insert image' component={inputField} />
                                    <ErrorMessage className="form-text" style={{color: 'red'}} name='image' component='small'/>
                                </div>
                                <button type="submit" disabled={isSubmitting} className="btn btn-primary mb-2">Submit</button>
                            </Form>

                        )}
                    </Formik>
                </div>
            </div>
        </div>
    )
}
export default addPost
