import React from 'react'
import { Link } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import yup from 'yup';
function addPost() {
    const check = yup.object({
        title: yup.string().re
    });
    return (
        <div className='container-sm'>
            <div className='container-sm'>

                <div className='row justify-content-center '>
                    <div className='col-12 col-sm-8 col-lg-6 d-flex justify-content-between align-items-center p-0'>
                        <p style={{ color: '#4a50c0', fontWeight: '500' }} className='mt-4 text-center fs-4'>Add new tool</p>
                        <Link to='/post' className='btn btn-primary'>Back</Link>
                    </div>
                </div>
                <div className='row justify-content-center '>
                    <Formik
                        initialValues={{
                            title: '',
                            content: '',
                            link: '',
                            category: '',
                            image: ''
                        }}
                        validate={value => {
                            const error = {};
                            if (value.title == '') {
                                error.title = 'required';
                            }
                            return error;
                        }}
                        onSubmit={(values, { setSubmitting }) => {
                            setTimeout(() => {
                                alert(JSON.stringify(values, null, 2));
                                setSubmitting(false);
                            }, 400);
                        }}
                    >
                        {({ isSubmitting }) => (
                            <Form className='col-12 col-sm-8 col-lg-6 border border-primary p-4 mt-3 rounded-2 shadow-sm'>
                                <Field name='title' type='text' />
                                <ErrorMessage name="title" component="div" />
                                <div className="mb-3">
                                    <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                                    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                                </div>
                                <div className=" row row-cols-1 row-cols-sm-2">
                                    <div className='col'>
                                        <div className='mb-3'>
                                            <label htmlFor="exampleInputPassword1" className="form-label">Link</label>
                                            <input type="password" className="form-control" id="exampleInputPassword1" />
                                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                                        </div>
                                    </div>
                                    <div className='col'>
                                        <div className="mb-3">
                                            <label htmlFor="exampleInputPassword1" className="form-label">Category</label>
                                            <select type="password" className="form-select" id="exampleInputPassword1">
                                                <option>dfdf1d</option>
                                                <option>dfdf2d</option>
                                                <option>dfdf3d</option>
                                            </select>
                                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                                        </div>
                                    </div>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="exampleInputPassword1" className="form-label">Content</label>
                                    <textarea type="password" className="form-control" id="exampleInputPassword1"></textarea>
                                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="exampleInputPassword1" className="form-label">Image</label>
                                    <input type='file' className="form-control" id="exampleInputPassword1" />
                                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                                </div>
                                <button type="submit" disabled={isSubmitting} className="btn btn-primary mb-2">Submit</button>
                            </Form>

                        )}
                    </Formik>
                </div>
            </div>
        </div>
    )
}
export default addPost
