import React from 'react';
import PropTypes from 'prop-types';
import {Link} from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';
import inputField from '../conFigForm/inputField';
categories.propTypes = {
    data: PropTypes.array.isRequired
}
export default function categories(props) {
    const Yub = yup.object({
        title: string().required('not null')
    })
    return (
        <div className='mb-3 col-12 col-sm-3'>
            <p className='fs-4'>Categories</p>
            <Formik>
                initialValues={{ title: '' }}
                validati
            </Formik>
            <Form>
                <input className='form-control'/>
                <button className='mt-3 mb-3 btn btn-primary'>Add new</button>
            </Form>
            <Link style={{ color: '#4a50c0', fontWeight: 500}} className='text-decoration-none d-block ' to='/post'>All</Link>
            {

                props.data.map((value,key)=>{
                    return <Link  style={{ color: '#4a50c0', fontWeight: 500}} className='text-decoration-none d-block' to={'?Idcategory='+value._id} key={key}>{value.title}</Link>
                }) 
            }
        </div>
    )
}
