import React from 'react'
import Search from '../../../components/search';
import Categories from '../../../components/categories';
import ElementPost from './../component/elementPost';
import { useDispatch as UseDispatch, useSelector as UseSelector } from 'react-redux';
import { useEffect as UseEffect} from 'react';
import actionpost from '../../../redux/actions/index.js';
import axios from 'axios';
export default function post() {
    const getState = UseSelector(state=>state.post);
    var postData = [] ;
    const search = ()=>{
        if(getState.data){
            console.log(getState);
            postData = getState.data.filter(value=>{
                if(value.title.toLowerCase().includes(getState.search.toLowerCase())){
                    return value;
                }
            });
        }
    }
    search();

    const dispatch = UseDispatch();
    UseEffect(()=>{
        let data = [];
        axios.get('http://localhost:4000/post')
        .then(e=>e.data).then(e=>{
            data = e;
        });
        setTimeout(() => {
            console.log(data);
            const getData = actionpost.getDataSuccess(data);
            dispatch(getData);
        }, 2000);
    },[]);
    return (
        <div>
            <Search />
            <div className='p-3 container-sm'>
                <div className='row'>
                    <Categories />
                    <div className='col-sm-9 col-12'>
                        <div className='row row-cols-lg-3 row-cols-sm-2 row-cols-1'>
                            {
                                postData.map((e, key)=>{
                                    return <ElementPost title = {e.title} content = {e.content} key={key}/>
                                })
                            }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
