import {getPost} from '../action/action';
const initialState = {
    post:{
        isLoading: false,
        data:[]
    }
};
export default function postReducer(state = initialState,action){
    switch (action.type) {
        case getPost.getPostRequest().type:
            
            break;
        case getPost.getPostSuccess().type:
                console.log(action.payload);
                return {
                    ...state,
                    isLoading: false,
                    data: action.payload
                }
        default:
            return state;
    }
}
