import React from 'react'
import PropTypes from 'prop-types'

fileField.propTypes = {
    field: PropTypes.object.isRequired,
    form: PropTypes.object.isRequired,
    
    // tự thêm props
    type: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    placeholder: PropTypes.string.isRequired
}

fileField.defaultProps = {
    type: 'text',
    label: '',
    placeholder: ''
}

const change = (event)=>{
    console.log(event.target.value);
    field.onChange({
        target:{
            name: props.field.name,
            value: event.target.value,
        }
    })
}

function fileField(props) {
    return (
        <div>
            <label htmlFor={props.field.name} className="form-label">{props.label}</label>
            <input {...props.field} type={props.type} onChange={()=>change()} className="form-control" placeholder={props.placeholder} id={props.field.name} aria-describedby="emailHelp" />
        </div>
    )
}

export default fileField
