import {getPost} from '../action/action';
const initialState = [];
export default postReducer = async (state = initialState,action)=>{
    switch (action.type) {
        case getPost.getPostRequest().type:
            
            break;
        case getPost.getPostSuccess().type:
                const r = [...state];
                const r1 = await action.payload;

                r.push(action.payload);
                return r;

        default:
            return state;
    }
}
