import React from 'react'
import {Link} from 'react-router-dom';
import {Formik, Form, Field, ErrorMessage} from 'formik';
function addPost() {
    return (
        <div className='container-sm'>
            <div className='container-sm'>

            <div className='row justify-content-center '>
                <div className='col-12 col-sm-8 col-lg-6 d-flex justify-content-between align-items-center p-0'>
                    <p style={{color: '#4a50c0', fontWeight:'500'}} className='mt-4 text-center fs-4'>Add new tool</p>
                    <Link to='/post' className='btn btn-primary'>Back</Link>
                </div>
            </div>
                <div className='row justify-content-center '>
                    <Formik
                        initialValues={{
                            title:'',
                            content: '',
                            link:'',
                            category:'',
                            image:''
                        }}
                        validate = {value=>{
                            const error = {};
                            if(value.title == ''){
                                error.title = 'required';
                            }
                            return error;
                        }}
                        onSubmit={(values, { setSubmitting }) => {
                            setTimeout(() => {
                              alert(JSON.stringify(values, null, 2));
                              setSubmitting(false);
                            }, 400);
                        }}
                    >
                    {({isSubmitting })=>{

                    }}
                    </Formik>
                </div>
            </div>
        </div>
    )
}
export default addPost
