import React from 'react'
import Search from '../../../components/search';
import Categories from '../../../components/categories';
import ElementPost from './../component/elementPost';
import { useDispatch as UseDispatch, useSelector as UseSelector } from 'react-redux';
import { useEffect as UseEffect} from 'react';
import actionpost from '../../../redux/actions/index.js';
import actionCategories from '../../../redux/actions/categories';
import axios from 'axios';


export default function post() {
    const dispatch = UseDispatch();

    // get state have data of posts
    const getState = UseSelector(state=>state.post);
    var postData = [] ;
    // search post by name
    const search = ()=>{
        if(getState.data){
            postData = getState.data.filter(value=>{
                if(value.title.toLowerCase().includes(getState.search.toLowerCase())){
                    return value;
                }
                return 0;
            });
        }
    }
    search();

    // get state have data of categories
    const getStateCategories = UseSelector(state=>state.categories).data;


    UseEffect(()=>{
        let data = [];
        let dataCategory = [];
        axios.get('http://localhost:4000/post')
        .then(e=>e.data).then(e=>{
            data = e;
        });
        axios.get('http://localhost:4000/categories')
        .then(e=>e.data).then(e=>{
            dataCategory = e;
        });

        setTimeout(() => {
            // save data of categories from state
            dispatch(actionCategories.getData(dataCategory));

            // save data of posts from state
            const getData = actionpost.getDataSuccess(data);
            dispatch(getData);
        }, 2000);
    },[]);



    return (
        <div >
            <Search />
            <div className='p-3 container-sm'>
                <div className='row'>
                    <Categories data={getStateCategories}/>
                    <div className='col-sm-9 col-12'>
                        <div className='row row-cols-lg-3 row-cols-sm-2 row-cols-1'>
                            {
                                postData.map((e, key)=>{
                                    return <ElementPost title = {e.title} id={e._id} content = {e.content} key={key}/>
                                })
                            }
                        </div>
                    </div>
                </div>
            </div>
            <button style={{bottom: 20, right: 20}} className='position-fixed rounded-circle btn btn-lg btn-primary'>
                <p className='fs-3 m-0'>+</p>
            </button>
        </div>
    )
}
